--  DDL for View DPD
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "RIGEL"."DPD" ("ACCOUNT_ID", "APPLICATION_NO", "DUE_DT", "SOURCE_OFFICE_ID", "PRODUCT_CODE", "PRODUCT_DESC1", "SCHEME_CODE", "AMOUNT_APPROVED", "TOTAL_PRIN_OS_BASE_AMOUNT", "DPD_BUCKET", "DPD_CLASSIFICATION_DESC2") AS 
  (

SELECT H.ACCOUNT_ID,
  HT.APPLICATION_NO,
  H.DUE_DT ,
  HAD.SOURCE_OFFICE_ID,
  HAF.PRODUCT_CODE,
  HAF.PRODUCT_DESC1,
  HAF.SCHEME_CODE,
  HAF.AMOUNT_APPROVED,
  HAS.TOTAL_PRIN_OS_BASE_AMOUNT,
  (
  CASE
    WHEN H.DPD_DAYS BETWEEN 1 AND 30
    THEN 'EVER X'
    WHEN H.DPD_DAYS BETWEEN 31 AND 60
    THEN '31-60'
    WHEN H.DPD_DAYS BETWEEN 61 AND 90
    THEN '61-90'
    WHEN H.DPD_DAYS BETWEEN 91 AND 120
    THEN '91-120'
    WHEN H.DPD_DAYS > 120
    THEN 'GREATER THAN 120'
  END) DPD_BUCKET,
  H.DPD_CLASSIFICATION_DESC2
FROM HI_COLLECTION_DPD H,
  HI_ACCOUNT_TXN HT,
  HI_APPLICATION_DEMOGRAPHICS HAD,
  HI_APPLICATION_FACILITIES HAF,
  HI_ACCOUNT_SNAPSHOT HAS
WHERE H.ACCOUNT_ID     = HT.ACCOUNT_ID
AND HAD.APPLICATION_NO = HT.APPLICATION_NO
AND HAF.APPLICATION_NO = HT.APPLICATION_NO
AND HAS.APPLICATION_NO = HT.APPLICATION_NO
AND HAS.TXN_DT = H.DUE_DT
AND HT.TXN_TYPE_ID     =101
AND HAD.ORG_ID         =777
AND H.ACTIVE           = 'Y'
AND HT.ACTIVE          = 'Y'
AND HAD.ACTIVE         = 'Y');
REM INSERTING into RIGEL.DPD
SET DEFINE OFF;
